// App.js
import React, { useEffect } from "react";
import Chat, { Bubble, useMessages } from "@chatui/core";
import '@chatui/core/dist/index.css';

import axios from "axios";

// 配置常量
const API_URL = "http://localhost:8000/chat"; // 后端API地址

export interface User {
  avatar?: string;
  name?: string;
  url?: string;
  [k: string]: any;
}

export default function App() {
  const { messages, appendMsg, setTyping } = useMessages([
    {
      type: 'text',
      content: { text: '你好，我是Hikari~' },
      user: {
        name: '光',
        avatar: './1.png',
      },
    },
  ]);

  // 发送消息
  async function handleSend(type, val) {
    if (type === "text" && val.trim()) {
      // 用户消息
      appendMsg({
        type: "text",
        content: { text: val },
        position: "right"
      });

      try {
        const response = await axios.post(API_URL, { text: val });
        const reply = response.data.reply;

        // 机器人回复
        appendMsg({
          type: "text",
          content: { text: reply }
        });
      } catch (error) {
        console.error("Error:", error);
        appendMsg({
          type: "text",
          content: { text: "\"光在睡觉，不要打扰她比较好...\"" }
        });
      }
    }
  }

  // 渲染消息内容
  function renderMessageContent(msg) {
    return <Bubble content={msg.content.text} />;
  }

  // 清理副作用，避免在组件卸载时发送消息
  useEffect(() => {
    return () => {
      setTyping(false);
    };
  }, [setTyping]);

  return (
    <Chat
      navbar={{ title: "Arcaea Ambassador" }}
      messages={messages}
      renderMessageContent={renderMessageContent}
      onSend={handleSend}
    />
  );
}
